'use strict';

module.exports = {
  parseBoolean: function parseBoolean(value) {
    return value === 'on' ? true : value === 'off' ? false : null;
  },
  parseProgramData: function parseProgramData(data) {
    return data.filter(function (point) {
      return point.active === 'on';
    }).map(function (point) {
      var hour = point.t / 60 | 0;
      var minute = point.t % 60;
      return {
        dow: ['Su', 'Mo', 'Tu', 'We', 'Th', 'Fr', 'Sa'].indexOf(point.d),
        time: ('00' + hour).slice(-2) + ':' + ('00' + minute).slice(-2),
        temp: point.T
      };
    });
  }
};